import React, { useState, useEffect } from "react";
import "./App.css";
import {
  saveCalculation,
  getHistory,
} from "./services/calculationService";

function App() {
  // ====== Состояния анкеты ======
  const [breadUnits, setBreadUnits] = useState("");
  const [glucose, setGlucose] = useState("");

  // ====== Результаты ======
  const [result, setResult] = useState(null);
  const [interpretation, setInterpretation] = useState("");

  // ====== История / статистика ======
  const [history, setHistory] = useState([]);

  // ====== Медицинские коэффициенты ======
  const insulinPerBU = 1.5; // коэффициент на 1 ХЕ
  const targetGlucose = 5.5; // целевая глюкоза
  const sensitivity = 2; // чувствительность к инсулину

  // ====== Загрузка истории при старте ======
  useEffect(() => {
    setHistory(getHistory());
  }, []);

  // ====== Расчёт дозы ======
  const calculateInsulin = () => {
    if (breadUnits === "" || glucose === "") {
      alert("Заполните все поля");
      return;
    }

    const dose =
      breadUnits * insulinPerBU +
      (glucose - targetGlucose) / sensitivity;

    const roundedDose = Number(dose.toFixed(2));
    setResult(roundedDose);

    // ====== Интерпретация ======
    if (roundedDose < 2) {
      setInterpretation("Низкая рекомендуемая доза");
    } else if (roundedDose <= 6) {
      setInterpretation("Рекомендуемая доза в пределах нормы");
    } else {
      setInterpretation(
        "Повышенная доза. Рекомендуется дополнительный контроль"
      );
    }

    // ====== Сохранение в БД (localStorage) ======
    saveCalculation(roundedDose);

    // ====== Обновление статистики ======
    setHistory(getHistory());
  };

  return (
    <div className="container">
      <h1>Калькулятор дозы инсулина</h1>

      {/* ====== Анкета ====== */}
      <label>Хлебные единицы (ХЕ)</label>
      <input
        type="number"
        value={breadUnits}
        onChange={(e) => setBreadUnits(e.target.value)}
        placeholder="Например: 4"
      />

      <label>Уровень глюкозы (ммоль/л)</label>
      <input
        type="number"
        value={glucose}
        onChange={(e) => setGlucose(e.target.value)}
        placeholder="Например: 7.8"
      />

      <button onClick={calculateInsulin}>
        Рассчитать дозу
      </button>

      {/* ====== Результат ====== */}
      {result !== null && (
        <div className="result">
          <p>Рекомендуемая доза инсулина:</p>
          <strong>{result} ЕД</strong>
          <p className="interpretation">{interpretation}</p>
        </div>
      )}

      {/* ====== Статистика ====== */}
      <h2>История расчётов</h2>

      {history.length === 0 ? (
        <p>История пока пуста</p>
      ) : (
        <ul>
          {history.map((item, index) => (
            <li key={index}>
              {item.date} — {item.dose} ЕД
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
