import { useState } from "react";
import { register, login } from "../services/api";

export default function Register({ setToken, setRole }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setLocalRole] = useState("patient");

  const submit = async () => {
    try {
      // регистрация
      await register({ email, password, role: localRole });

      // логин сразу после регистрации
      const res = await login({ email, password });

      // сохранение данных
      localStorage.setItem("token", res.token);
      localStorage.setItem("role", res.role);

      setToken(res.token);
      setRole(res.role);
    } catch (e) {
      alert("Ошибка регистрации");
      console.error(e);
    }
  };

  return (
    <div className="container">
      <h1>Регистрация</h1>

      <input
        placeholder="Email"
        value={email}
        onChange={e => setEmail(e.target.value)}
      />

      <input
        type="password"
        placeholder="Пароль"
        value={password}
        onChange={e => setPassword(e.target.value)}
      />

      <select value={localRole} onChange={e => setLocalRole(e.target.value)}>
        <option value="patient">Пациент</option>
        <option value="doctor">Медперсонал</option>
      </select>

      <button onClick={submit}>Зарегистрироваться</button>
    </div>
  );
}
