import React, { useEffect, useState } from "react";
import { getMyHistory } from "../services/api";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend
);

export default function History({ token }) {
  const [history, setHistory] = useState([]);
  const [period, setPeriod] = useState(7);

  useEffect(() => {
    if (!token) return;

    getMyHistory(token).then((data) => {
      setHistory(data || []);
    });
  }, [token]);

  const filtered = history.filter((h) => {
    const date = new Date(h.created_at); // üî• –í–ê–ñ–ù–û
    const diff =
      (Date.now() - date.getTime()) / (1000 * 60 * 60 * 24);
    return diff <= period;
  });

  const chartData = {
    labels: filtered.map((h) =>
      new Date(h.created_at).toLocaleDateString()
    ),
    datasets: [
      {
        label: "–ì–ª—é–∫–æ–∑–∞",
        data: filtered.map((h) => Number(h.glucose)),
        tension: 0.4,
      },
    ],
  };

  return (
    <div className="container">
      <h2>–ò—Å—Ç–æ—Ä–∏—è</h2>

      <select
        value={period}
        onChange={(e) => setPeriod(Number(e.target.value))}
      >
        <option value={7}>7 –¥–Ω–µ–π</option>
        <option value={30}>30 –¥–Ω–µ–π</option>
        <option value={90}>3 –º–µ—Å—è—Ü–∞</option>
      </select>

      {filtered.length > 0 ? (
        <Line data={chartData} />
      ) : (
        <p className="muted">–ù–µ—Ç –¥–∞–Ω–Ω—ã—Ö –∑–∞ –≤—ã–±—Ä–∞–Ω–Ω—ã–π –ø–µ—Ä–∏–æ–¥</p>
      )}
    </div>
  );
}
