import React, { useEffect, useState, useCallback } from "react";
import { getProfile, updateProfile } from "../services/api";

export default function Profile({ token }) {
  const [profile, setProfile] = useState(null);
  const [form, setForm] = useState({});
  const [edit, setEdit] = useState(false);

  const loadProfile = useCallback(() => {
    if (!token) return;

    getProfile(token).then(data => {
      setProfile(data);
      setForm(data || {});
    });
  }, [token]);

  useEffect(() => {
    loadProfile();
  }, [loadProfile, token]); // ✅ ESLint исправлен

  const save = async () => {
    await updateProfile(form, token);
    setEdit(false);
    loadProfile();
  };

  if (!profile && !edit) {
    return <p>Загрузка профиля...</p>;
  }

  return (
    <div className="container">
      <h2>Профиль</h2>

      {!edit ? (
        <>
          {profile?.photo && (
            <div className="profile-avatar">
            <img
              src={profile.photo}
              alt="Фото профиля"
              style={{ width: 120, borderRadius: "50%" }}
            />
            </div>
          )}

          <p>Рост: {profile?.height || "—"}</p>
          <p>Вес: {profile?.weight || "—"}</p>
          <p>Заболевания: {profile?.diseases || "—"}</p>

          <button onClick={() => setEdit(true)}>Редактировать</button>
        </>
      ) : (
        <>
          <input
            placeholder="Рост"
            value={form.height || ""}
            onChange={e => setForm({ ...form, height: e.target.value })}
          />
          <input
            placeholder="Вес"
            value={form.weight || ""}
            onChange={e => setForm({ ...form, weight: e.target.value })}
          />
          <textarea
            placeholder="Заболевания"
            value={form.diseases || ""}
            onChange={e => setForm({ ...form, diseases: e.target.value })}
          />

          <input
            type="file"
            accept="image/*"
            onChange={e => {
              const reader = new FileReader();
              reader.onload = () =>
                setForm({ ...form, photo: reader.result });
              reader.readAsDataURL(e.target.files[0]);
            }}
          />

          <button onClick={save}>Сохранить</button>
          <button onClick={() => setEdit(false)}>Отмена</button>
        </>
      )}
    </div>
  );
}
