import React, { useState } from "react";
import "./App.css";

function App() {
  const [breadUnits, setBreadUnits] = useState("");
  const [glucose, setGlucose] = useState("");
  const [result, setResult] = useState(null);

  const insulinPerBU = 1.5;
  const targetGlucose = 5.5;
  const sensitivity = 2;

  const calculateInsulin = () => {
    const dose =
      breadUnits * insulinPerBU +
      (glucose - targetGlucose) / sensitivity;

    setResult(dose.toFixed(2));
  };

  return (
    <div className="container">
      <h1>Калькулятор дозы инсулина</h1>

      <label>Хлебные единицы (ХЕ)</label>
      <input
        type="number"
        value={breadUnits}
        onChange={(e) => setBreadUnits(e.target.value)}
      />

      <label>Уровень глюкозы (ммоль/л)</label>
      <input
        type="number"
        value={glucose}
        onChange={(e) => setGlucose(e.target.value)}
      />

      <button onClick={calculateInsulin}>
        Рассчитать дозу
      </button>

      {result && (
        <div className="result">
          <p>Рекомендуемая доза инсулина:</p>
          <strong>{result} ЕД</strong>
        </div>
      )}
    </div>
  );
}

export default App;
