import { useEffect, useState } from "react";
import { getHistory } from "../services/api";
import { Line } from "react-chartjs-2";
import "chart.js/auto";

export default function History({ token }) {
  const [history, setHistory] = useState([]);

  useEffect(() => {
    getHistory(token).then(setHistory);
  }, [token]);

  const chartData = {
    labels: history.map((h) => h.created_at),
    datasets: [
      {
        label: "Глюкоза (ммоль/л)",
        data: history.map((h) => h.glucose),
        borderColor: "#2563eb",
      },
    ],
  };

  return (
    <div className="container">
      <h1>История</h1>

      {history.length > 0 && <Line data={chartData} />}

      {history.map((h) => (
        <div className="card" key={h.id}>
          <div className="muted">{h.created_at}</div>
          <div className="result-value">{h.dose} ЕД</div>
          <div>{h.interpretation}</div>
        </div>
      ))}
    </div>
  );
}
