import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import {
  getPatientProfile,
  approvePatient,
} from "../services/api";

export default function PatientProfile({ token }) {
  const { id } = useParams();
  const [profile, setProfile] = useState(null);

  useEffect(() => {
    getPatientProfile(id, token).then(setProfile);
  }, [id, token]);

  if (!profile) {
    return <div className="container">–ó–∞–≥—Ä—É–∑–∫–∞...</div>;
  }

  return (
    <div className="container">
      <h1>–ö–∞—Ä—Ç–æ—á–∫–∞ –ø–∞—Ü–∏–µ–Ω—Ç–∞</h1>

      {profile.photo && (
        <div className="profile-avatar">
          <img src={profile.photo} alt="–§–æ—Ç–æ –ø–∞—Ü–∏–µ–Ω—Ç–∞" />
        </div>
      )}

      <div className="card">
        <strong>Email:</strong>
        <div>{profile.email}</div>
      </div>

      <div className="card">
        <strong>–†–æ—Å—Ç:</strong> {profile.height || "‚Äî"} —Å–º
      </div>

      <div className="card">
        <strong>–í–µ—Å:</strong> {profile.weight || "‚Äî"} –∫–≥
      </div>

      <div className="card">
        <strong>–ó–∞–±–æ–ª–µ–≤–∞–Ω–∏—è:</strong>
        <div>{profile.diseases || "‚Äî"}</div>
      </div>

      <div className="card">
        <strong>–°—Ç–∞—Ç—É—Å:</strong>{" "}
        {profile.approved ? "üü¢ –ü–æ–¥—Ç–≤–µ—Ä–∂–¥—ë–Ω" : "üü† –û–∂–∏–¥–∞–µ—Ç –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏—è"}
      </div>

      {!profile.approved && (
        <button
          onClick={async () => {
            await approvePatient(id, token);
            setProfile({ ...profile, approved: 1 });
          }}
        >
          –ü–æ–¥—Ç–≤–µ—Ä–¥–∏—Ç—å –ø–∞—Ü–∏–µ–Ω—Ç–∞
        </button>
      )}
    </div>
  );
}
