import React from "react";
import { NavLink } from "react-router-dom";
import { FaCalculator, FaUser, FaHistory, FaUsers, FaSignOutAlt } from "react-icons/fa";

export default function BottomNav({ role, onLogout }) {
  return (
    <nav className="bottom-nav">

      <NavLink to="/calculator" style={styles.link}>
        <FaCalculator />
        <span>Калькулятор</span>
      </NavLink>

      <NavLink to="/history" style={styles.link}>
        <FaHistory />
        <span>История</span>
      </NavLink>

      <NavLink to="/profile" style={styles.link}>
        <FaUser />
        <span>Профиль</span>
      </NavLink>

      {role === "doctor" && (
        <NavLink to="/patients" style={styles.link}>
          <FaUsers />
          <span>Пациенты</span>
        </NavLink>
      )}

      <button onClick={onLogout} style={styles.logout}>
        <FaSignOutAlt />
      </button>
    </nav>
  );
}

const styles = {
  nav: {
    position: "fixed",
    bottom: 0,
    left: 0,
    right: 0,
    height: 60,
    background: "#ffffff",
    display: "flex",
    justifyContent: "space-around",
    alignItems: "center",
    borderTop: "1px solid #ddd",
  },
  link: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    fontSize: 12,
    color: "#333",
    textDecoration: "none",
  },
  logout: {
    background: "none",
    border: "none",
    fontSize: 18,
    color: "#d00",
  },
};
