import { useEffect, useState } from "react";
import { getPatients, getPatientProfile, approvePatient } from "../services/api";

export default function Patients({ token }) {
  const [patients, setPatients] = useState([]);
  const [selected, setSelected] = useState(null);

  useEffect(() => {
    getPatients(token).then(setPatients);
  }, [token]);

  const openPatient = async (id) => {
    const data = await getPatientProfile(id, token);
    setSelected(data);
  };

  const approve = async (id) => {
    await approvePatient(id, token);
    alert("Пациент подтверждён");
  };

  return (
    <div className="container">
      <h1>Пациенты</h1>

      {!selected &&
        patients.map((p) => (
          <div key={p.id} className="card" onClick={() => openPatient(p.id)}>
            <p>{p.email}</p>
            <p>{p.approved ? "✅ Подтверждён" : "⏳ Не подтверждён"}</p>
          </div>
        ))}

      {selected && (
        <div className="card">
          {selected.photo && <img src={selected.photo} width={120} />}
          <p><b>Рост:</b> {selected.height}</p>
          <p><b>Вес:</b> {selected.weight}</p>
          <p><b>Заболевания:</b> {selected.diseases}</p>

          {!selected.approved && (
            <button onClick={() => approve(selected.id)}>
              Подтвердить пациента
            </button>
          )}

          <button onClick={() => setSelected(null)}>Назад</button>
        </div>
      )}
    </div>
  );
}
