import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  getPatients,
  getPatientHistory,
} from "../services/api";

export default function Patients({ token }) {
  const navigate = useNavigate();

  const [patients, setPatients] = useState([]);
  const [lastMap, setLastMap] = useState({});
  const [activeTab, setActiveTab] = useState("pending"); // pending | all
  const [search, setSearch] = useState("");

  useEffect(() => {
    getPatients(token).then(async (rows) => {
      const list = rows || [];
      setPatients(list);

      const map = {};

      for (const p of list) {
        const history = await getPatientHistory(
          p.id,
          token
        );

        if (history && history.length > 0) {
          const last = [...history].sort(
            (a, b) =>
              new Date(b.created_at) -
              new Date(a.created_at)
          )[0];
          map[p.id] = last;
        }
      }

      setLastMap(map);
    });
  }, [token]);

  /* üîÄ –§–ò–õ–¨–¢–† –ü–û –¢–ê–ë–£ */
  const tabFiltered =
    activeTab === "pending"
      ? patients.filter(
          (p) =>
            lastMap[p.id] &&
            !lastMap[p.id].dose_approved
        )
      : patients;

  /* üîç –ü–û–ò–°–ö –ü–û EMAIL + –§–ò–û */
  const filteredPatients = tabFiltered.filter(
    (p) => {
      const text = (
        p.email +
        " " +
        (p.name || "")
      ).toLowerCase();

      return text.includes(
        search.toLowerCase()
      );
    }
  );

  return (
    <div className="container">
      <h2>–ü–∞—Ü–∏–µ–Ω—Ç—ã</h2>

      {/* üîç –ü–û–ò–°–ö */}
      <input
        placeholder="–ü–æ–∏—Å–∫ –ø–æ –§–ò–û –∏–ª–∏ email"
        value={search}
        onChange={(e) =>
          setSearch(e.target.value)
        }
      />

      {/* üîÄ –¢–ê–ë–´ */}
      <div className="tabs">
        <button
          className={
            activeTab === "pending"
              ? "active"
              : ""
          }
          onClick={() =>
            setActiveTab("pending")
          }
        >
          –ù–µ–ø–æ–¥—Ç–≤–µ—Ä–∂–¥—ë–Ω–Ω—ã–µ
        </button>

        <button
          className={
            activeTab === "all"
              ? "active"
              : ""
          }
          onClick={() =>
            setActiveTab("all")
          }
        >
          –í—Å–µ
        </button>
      </div>

      {/* üìã –°–ü–ò–°–û–ö */}
      {filteredPatients.length === 0 ? (
        <p className="muted">
          –ü–∞—Ü–∏–µ–Ω—Ç—ã –Ω–µ –Ω–∞–π–¥–µ–Ω—ã
        </p>
      ) : (
        filteredPatients.map((p) => {
          const last = lastMap[p.id];

          return (
            <div
              key={p.id}
              className="card patient-card"
              onClick={() =>
                navigate(`/patients/${p.id}`)
              }
            >
              <strong>
                {p.name || "–ü–∞—Ü–∏–µ–Ω—Ç"}
              </strong>

              <div className="muted">
                {p.email}
              </div>

              {last ? (
                <>
                  <div className="muted">
                    –°–∞—Ö–∞—Ä: {last.glucose}
                  </div>

                  <div className="muted">
                    –ò–Ω—Å—É–ª–∏–Ω:{" "}
                    {last.dose ?? "‚Äî"} –µ–¥.
                  </div>

                  {!last.dose_approved && (
                    <div className="badge warning">
                      –¢—Ä–µ–±—É–µ—Ç –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏—è
                    </div>
                  )}
                </>
              ) : (
                <div className="muted">
                  –ù–µ—Ç –∏–∑–º–µ—Ä–µ–Ω–∏–π
                </div>
              )}
            </div>
          );
        })
      )}
    </div>
  );
}
