import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { register, login } from "../services/api";

export default function Register({ setToken, setRole }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [userRole, setUserRole] = useState("patient");

  const navigate = useNavigate();

  const submit = async () => {
    if (!email || !password) return;

    // 1️⃣ регистрация
    await register({
      email,
      password,
      role: userRole,
    });

    // 2️⃣ автоматический вход
    const res = await login({ email, password });

    // 3️⃣ сохраняем данные
    localStorage.setItem("token", res.token);
    localStorage.setItem("role", res.role);

    setToken(res.token);
    setRole(res.role);

    // 4️⃣ переход в калькулятор
    navigate("/calculator");
  };

  return (
    <div className="container">
      <h1>Регистрация</h1>

      <input
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />

      <input
        type="password"
        placeholder="Пароль"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />

      <select
        value={userRole}
        onChange={(e) => setUserRole(e.target.value)}
      >
        <option value="patient">Пациент</option>
        <option value="doctor">Медперсонал</option>
      </select>

      <button onClick={submit}>Зарегистрироваться</button>
    </div>
  );
}
