import { useEffect, useState } from "react";
import { getProfile, updateProfile } from "../services/api";

export default function Profile({ token }) {
  const [profile, setProfile] = useState({});
  const [edit, setEdit] = useState(false);
  const [loading, setLoading] = useState(true);

  const [height, setHeight] = useState("");
  const [weight, setWeight] = useState("");
  const [diseases, setDiseases] = useState("");
  const [photo, setPhoto] = useState("");

  useEffect(() => {
    loadProfile();
    // eslint-disable-next-line
  }, []);

  const loadProfile = async () => {
    setLoading(true);
    const data = await getProfile(token);

    setProfile(data || {});
    setHeight(data?.height || "");
    setWeight(data?.weight || "");
    setDiseases(data?.diseases || "");
    setPhoto(data?.photo || "");

    setLoading(false);
  };

  const uploadPhoto = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = () => setPhoto(reader.result);
    reader.readAsDataURL(file);
  };

  const save = async () => {
    await updateProfile({ height, weight, diseases, photo }, token);
    await loadProfile();
    setEdit(false);
  };

  if (loading) {
    return (
      <div className="container">
        <p>Загрузка профиля...</p>
      </div>
    );
  }

  return (
    <div className="container">
      <h1>Профиль</h1>

      {/* ===== КАРТОЧКА ===== */}
      {!edit && (
        <div className="card">
          {profile.photo ? (
            <img
              src={profile.photo}
              alt="avatar"
              style={{
                width: 120,
                height: 120,
                borderRadius: "50%",
                objectFit: "cover",
                marginBottom: 12,
              }}
            />
          ) : (
            <div
              style={{
                width: 120,
                height: 120,
                borderRadius: "50%",
                background: "#ddd",
                marginBottom: 12,
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
              }}
            >
              Фото
            </div>
          )}

          <p><b>Рост:</b> {profile.height || "не указан"} см</p>
          <p><b>Вес:</b> {profile.weight || "не указан"} кг</p>

          <p><b>Хронические заболевания:</b></p>
          <p className="muted">
            {profile.diseases || "не указаны"}
          </p>

          <button onClick={() => setEdit(true)}>
            Редактировать
          </button>
        </div>
      )}

      {/* ===== ФОРМА ===== */}
      {edit && (
        <div className="card">
          {photo && (
            <img
              src={photo}
              alt="avatar"
              style={{
                width: 120,
                height: 120,
                borderRadius: "50%",
                objectFit: "cover",
                marginBottom: 12,
              }}
            />
          )}

          <input type="file" accept="image/*" onChange={uploadPhoto} />

          <input
            placeholder="Рост (см)"
            value={height}
            onChange={(e) => setHeight(e.target.value)}
          />

          <input
            placeholder="Вес (кг)"
            value={weight}
            onChange={(e) => setWeight(e.target.value)}
          />

          <textarea
            placeholder="Хронические заболевания"
            value={diseases}
            onChange={(e) => setDiseases(e.target.value)}
          />

          <button onClick={save}>Сохранить</button>
        </div>
      )}
    </div>
  );
}
