import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  getPatients,
  getPatientHistory,
} from "../services/api";

export default function Patients({ token }) {
  const navigate = useNavigate();

  const [patients, setPatients] = useState([]);
  const [activeTab, setActiveTab] = useState("pending"); // pending | all
  const [lastMap, setLastMap] = useState({});

  useEffect(() => {
    getPatients(token).then(async (rows) => {
      setPatients(rows || []);

      // üî• –ø–æ–¥–≥—Ä—É–∂–∞–µ–º –ø–æ—Å–ª–µ–¥–Ω–µ–µ –∏–∑–º–µ—Ä–µ–Ω–∏–µ –¥–ª—è –∫–∞–∂–¥–æ–≥–æ
      const map = {};

      for (const p of rows || []) {
        const history = await getPatientHistory(
          p.id,
          token
        );

        if (history && history.length > 0) {
          const last = [...history].sort(
            (a, b) =>
              new Date(b.created_at) -
              new Date(a.created_at)
          )[0];
          map[p.id] = last;
        }
      }

      setLastMap(map);
    });
  }, [token]);

  /* üîç –§–ò–õ–¨–¢–† */
  const filteredPatients =
    activeTab === "pending"
      ? patients.filter(
          (p) =>
            lastMap[p.id] &&
            !lastMap[p.id].dose_approved
        )
      : patients;

  return (
    <div className="container">
      <h2>–ü–∞—Ü–∏–µ–Ω—Ç—ã</h2>

      {/* üîÄ –ü–ï–†–ï–ö–õ–Æ–ß–ê–¢–ï–õ–¨ */}
      <div
        style={{
          display: "flex",
          gap: 8,
          marginBottom: 16,
        }}
      >
        <button
          className={
            activeTab === "pending" ? "" : "secondary"
          }
          onClick={() => setActiveTab("pending")}
        >
          –ù–µ–ø–æ–¥—Ç–≤–µ—Ä–∂–¥—ë–Ω–Ω—ã–µ
        </button>

        <span
          style={{
            alignSelf: "center",
            fontWeight: 600,
          }}
        >
          /
        </span>

        <button
          className={
            activeTab === "all" ? "" : "secondary"
          }
          onClick={() => setActiveTab("all")}
        >
          –í—Å–µ
        </button>
      </div>

      {/* üìã –°–ü–ò–°–û–ö */}
      {filteredPatients.length === 0 ? (
        <p className="muted">
          –ù–µ—Ç –ø–∞—Ü–∏–µ–Ω—Ç–æ–≤ –¥–ª—è –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–∏—è
        </p>
      ) : (
        filteredPatients.map((p) => {
          const last = lastMap[p.id];

          return (
            <div
              key={p.id}
              className="card"
              onClick={() =>
                navigate(`/patients/${p.id}`)
              }
              style={{ cursor: "pointer" }}
            >
              <strong>{p.email}</strong>
              <br />

              {last ? (
                <>
                  –°–∞—Ö–∞—Ä: {last.glucose}
                  <br />
                  –ò–Ω—Å—É–ª–∏–Ω: {last.dose ?? "‚Äî"} –µ–¥.
                  <br />

                  {!last.dose_approved && (
                    <span
                      style={{
                        color: "orange",
                        fontWeight: 600,
                      }}
                    >
                      ‚è≥ –¢—Ä–µ–±—É–µ—Ç –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏—è
                    </span>
                  )}
                </>
              ) : (
                <span className="muted">
                  –ù–µ—Ç –∏–∑–º–µ—Ä–µ–Ω–∏–π
                </span>
              )}
            </div>
          );
        })
      )}
    </div>
  );
}
