import { useEffect, useState } from "react";
import {
  getPatients,
  getPatientHistory,
  getPatientProfile,
} from "../services/api";
import jsPDF from "jspdf";

export default function Patients({ token }) {
  const [patients, setPatients] = useState([]);
  const [profile, setProfile] = useState(null);
  const [history, setHistory] = useState([]);

  useEffect(() => {
    getPatients(token).then(setPatients);
  }, [token]);

  const selectPatient = async (id) => {
    const p = await getPatientProfile(id, token);
    const h = await getPatientHistory(id, token);
    setProfile(p);
    setHistory(h);
  };

  const exportPDF = () => {
    const pdf = new jsPDF();
    pdf.text("Отчёт пациента", 10, 10);

    if (profile) {
      pdf.text(`Email: ${profile.email}`, 10, 20);
      pdf.text(`Рост: ${profile.height || "-"}`, 10, 28);
      pdf.text(`Вес: ${profile.weight || "-"}`, 10, 36);
    }

    history.forEach((h, i) => {
      pdf.text(
        `${h.created_at}: ${h.glucose} → ${h.dose} ЕД`,
        10,
        50 + i * 8
      );
    });

    pdf.save("patient-report.pdf");
  };

  return (
    <div className="container">
      <h1>Пациенты</h1>

      {patients.map((p) => (
        <button
          key={p.id}
          className="secondary"
          onClick={() => selectPatient(p.id)}
        >
          {p.email}
        </button>
      ))}

      {profile && (
        <>
          <h2>Профиль пациента</h2>
          <div className="card">
            <p><b>Email:</b> {profile.email}</p>
            <p><b>Рост:</b> {profile.height || "-"}</p>
            <p><b>Вес:</b> {profile.weight || "-"}</p>
            <p><b>Заболевания:</b></p>
            <p className="muted">{profile.diseases || "Не указаны"}</p>
          </div>

          <button onClick={exportPDF}>Скачать PDF</button>
        </>
      )}

      {history.map((h) => (
        <div className="card" key={h.id}>
          <div className="muted">{h.created_at}</div>
          <div className="result-value">{h.dose} ЕД</div>
          <div>{h.interpretation}</div>
        </div>
      ))}
    </div>
  );
}
