import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import {
  getPatientProfile,
  getPatientHistory,
  approveDose,
} from "../services/api";
import { Line } from "react-chartjs-2";
import { generatePatientPDF } from "../utils/pdf";

export default function PatientProfile({ token }) {
  const { id } = useParams();

  const [profile, setProfile] = useState(null);
  const [history, setHistory] = useState([]);
  const [lastMeasurement, setLastMeasurement] = useState(null);

  useEffect(() => {
    getPatientProfile(id, token).then(setProfile);

    getPatientHistory(id, token).then((data) => {
      const rows = data || [];
      setHistory(rows);

      if (rows.length > 0) {
        const last = [...rows].sort(
          (a, b) =>
            new Date(b.created_at) - new Date(a.created_at)
        )[0];
        setLastMeasurement(last);
      }
    });
  }, [id, token]);

  if (!profile) {
    return <div className="container">–ó–∞–≥—Ä—É–∑–∫–∞...</div>;
  }

  /* üìà –ì–†–ê–§–ò–ö */
  const chartData = {
    labels: history.map((h) =>
      new Date(h.created_at).toLocaleDateString()
    ),
    datasets: [
      {
        label: "–ì–ª—é–∫–æ–∑–∞",
        data: history.map((h) => Number(h.glucose)),
        tension: 0.4,
        borderWidth: 2,
      },
      {
        label: "–ò–Ω—Å—É–ª–∏–Ω",
        data: history.map((h) =>
          h.dose !== null ? Number(h.dose) : null
        ),
        tension: 0.4,
        borderWidth: 2,
      },
    ],
  };

  return (
    <div className="container">
      <h2>–ú–µ–¥–∫–∞—Ä—Ç–∞ –ø–∞—Ü–∏–µ–Ω—Ç–∞</h2>

      {/* üßë –§–û–¢–û */}
      {profile.photo && (
        <div className="profile-avatar">
          <img src={profile.photo} alt="–ü–∞—Ü–∏–µ–Ω—Ç" />
        </div>
      )}

      {/* üßæ –ü–†–û–§–ò–õ–¨ */}
      <div className="card">
        <strong>Email:</strong> {profile.email} <br />
        <strong>–†–æ—Å—Ç:</strong> {profile.height || "‚Äî"} <br />
        <strong>–í–µ—Å:</strong> {profile.weight || "‚Äî"} <br />
        <strong>–ó–∞–±–æ–ª–µ–≤–∞–Ω–∏—è:</strong>{" "}
        {profile.diseases || "‚Äî"}
      </div>

      {/* üíâ –ü–û–°–õ–ï–î–ù–ï–ï –ò–ó–ú–ï–†–ï–ù–ò–ï */}
      {lastMeasurement && (
        <div className="card">
          <strong>–ü–æ—Å–ª–µ–¥–Ω–µ–µ –∏–∑–º–µ—Ä–µ–Ω–∏–µ</strong>
          <br />
          –°–∞—Ö–∞—Ä: {lastMeasurement.glucose} <br />
          –ò–Ω—Å—É–ª–∏–Ω: {lastMeasurement.dose ?? "‚Äî"} –µ–¥.
          <br />

          <span className="muted">
            {new Date(
              lastMeasurement.created_at
            ).toLocaleString()}
          </span>

          <br />

          {/* ‚úÖ –°–¢–ê–¢–£–° / –ö–ù–û–ü–ö–ê */}
          {lastMeasurement.dose_approved ? (
            <span
              style={{
                color: "green",
                fontWeight: 600,
              }}
            >
              ‚úÖ –î–æ–∑–∞ –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∞ –≤—Ä–∞—á–æ–º
            </span>
          ) : (
            <button
              style={{ marginTop: 10 }}
              onClick={async () => {
                await approveDose(
                  lastMeasurement.id,
                  token
                );
                setLastMeasurement({
                  ...lastMeasurement,
                  dose_approved: 1,
                });
              }}
            >
              ‚úÖ –ü–æ–¥—Ç–≤–µ—Ä–¥–∏—Ç—å –¥–æ–∑—É
            </button>
          )}
        </div>
      )}

      {/* üìà –ì–†–ê–§–ò–ö */}
      {history.length > 0 && (
        <div className="card">
          <Line data={chartData} />
        </div>
      )}

      {/* üìÑ PDF */}
      <button
        style={{ marginTop: 12 }}
        onClick={() =>
          generatePatientPDF({
            profile,
            history,
          })
        }
      >
        üìÑ –°–∫–∞—á–∞—Ç—å PDF –æ—Ç—á—ë—Ç
      </button>
    </div>
  );
}
