import { useEffect, useState } from "react";
import {
  getPatients,
  getPatientHistory,
  getPatientProfile,
} from "../services/api";

export default function Patients({ token }) {
  const [patients, setPatients] = useState([]);
  const [history, setHistory] = useState([]);
  const [profile, setProfile] = useState(null);

  useEffect(() => {
    getPatients(token).then(setPatients);
  }, [token]);

  const selectPatient = async (id) => {
    const h = await getPatientHistory(id, token);
    const p = await getPatientProfile(id, token);
    setHistory(h);
    setProfile(p);
  };

  return (
    <div className="container">
      <h1>Пациенты</h1>

      {patients.map((p) => (
        <button
          key={p.id}
          className="secondary"
          onClick={() => selectPatient(p.id)}
        >
          {p.email}
        </button>
      ))}

      {profile && (
        <>
          <h2>Профиль пациента</h2>
          <div className="card">
            <p><strong>Email:</strong> {profile.email}</p>
            <p><strong>Рост:</strong> {profile.height || "-"} см</p>
            <p><strong>Вес:</strong> {profile.weight || "-"} кг</p>
            <p><strong>Заболевания:</strong></p>
            <p className="muted">{profile.diseases || "Не указаны"}</p>
          </div>
        </>
      )}

      {history.map((h) => (
        <div className="card" key={h.id}>
          <p className="muted">{h.created_at}</p>
          <div className="result-value">{h.dose} ЕД</div>
          <p>{h.interpretation}</p>
        </div>
      ))}
    </div>
  );
}
