import { useEffect, useState } from "react";
import { getMyHistory } from "../services/api";
import { Line } from "react-chartjs-2";
import "chart.js/auto";

export default function History({ token }) {
  const [history, setHistory] = useState([]);
  const [period, setPeriod] = useState("all");

  useEffect(() => {
    getMyHistory(token).then(setHistory);
  }, [token]);

  const filterByPeriod = (data) => {
    const now = new Date();

    return data.filter((h) => {
      const date = new Date(h.created_at);

      if (period === "day") {
        return now - date <= 24 * 60 * 60 * 1000;
      }

      if (period === "week") {
        return now - date <= 7 * 24 * 60 * 60 * 1000;
      }

      if (period === "month") {
        return now - date <= 30 * 24 * 60 * 60 * 1000;
      }

      return true;
    });
  };

  const filtered = filterByPeriod(history);

  const chartData = {
    labels: filtered.map((h) => h.created_at),
    datasets: [
      {
        label: "Глюкоза (ммоль/л)",
        data: filtered.map((h) => h.glucose),
        borderColor: "#2563eb",
      },
    ],
  };

  return (
    <div className="container">
      <h1>История</h1>

      {/* ===== Выбор периода ===== */}
      <select value={period} onChange={(e) => setPeriod(e.target.value)}>
        <option value="day">За день</option>
        <option value="week">За неделю</option>
        <option value="month">За месяц</option>
        <option value="all">За всё время</option>
      </select>

      {filtered.length > 0 && <Line data={chartData} />}

      {filtered.map((h) => (
        <div className="card" key={h.id}>
          <div className="muted">{h.created_at}</div>
          <div className="result-value">{h.dose} ЕД</div>
          <div>{h.interpretation}</div>
        </div>
      ))}
    </div>
  );
}
