import { useEffect, useState } from "react";
import { getProfile, updateProfile } from "../services/api";

export default function Profile({ token }) {
  const [height, setHeight] = useState("");
  const [weight, setWeight] = useState("");
  const [diseases, setDiseases] = useState("");

  useEffect(() => {
    getProfile(token).then(p => {
      if (!p) return;
      setHeight(p.height || "");
      setWeight(p.weight || "");
      setDiseases(p.diseases || "");
    });
  }, [token]);

  const save = async () => {
    await updateProfile({ height, weight, diseases }, token);
    alert("Данные сохранены");
  };

  return (
    <div className="container">
      <h1>Личный профиль</h1>

      <input placeholder="Рост (см)" value={height} onChange={e => setHeight(e.target.value)} />
      <input placeholder="Вес (кг)" value={weight} onChange={e => setWeight(e.target.value)} />

      <textarea
        placeholder="Хронические заболевания, комментарии"
        value={diseases}
        onChange={e => setDiseases(e.target.value)}
      />

      <button onClick={save}>Сохранить</button>
    </div>
  );
}
