import React, { useEffect, useState } from "react";
import { getPatients } from "../services/api";
import { useNavigate } from "react-router-dom";

export default function Patients({ token }) {
  const [patients, setPatients] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    if (!token) return;

    getPatients(token).then((data) => {
      setPatients(data || []);
    });
  }, [token]);

  return (
    <div className="container">
      <h2>Пациенты</h2>

      {patients.length === 0 && (
        <p className="muted">Пациенты не найдены</p>
      )}

      {patients.map((p) => (
        <div
          key={p.id}
          className="card"
          style={{ cursor: "pointer" }}
          onClick={() => navigate(`/patients/${p.id}`)} // ✅ ПЕРЕХОД
        >
          <strong>{p.email}</strong>
          <div className="muted">
            {p.approved ? "Подтверждён врачом" : "Не подтверждён"}
          </div>
        </div>
      ))}
    </div>
  );
}
