import React, { useEffect, useState } from "react";
import { getMyHistory } from "../services/api";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend,
} from "chart.js";

// регистрация Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend
);

export default function History({ token }) {
  const [history, setHistory] = useState([]);
  const [period, setPeriod] = useState(7);

  useEffect(() => {
    if (!token) return;

    getMyHistory(token).then((data) => {
      setHistory(Array.isArray(data) ? data : []);
    });
  }, [token]);

  // фильтрация по периоду
  const filtered = history.filter((h) => {
    if (!h.date) return false;

    const daysDiff =
      (Date.now() - new Date(h.date).getTime()) /
      (1000 * 60 * 60 * 24);

    return daysDiff <= period;
  });

  const chartData = {
    labels: filtered.map((h) =>
      new Date(h.date).toLocaleDateString("ru-RU")
    ),
    datasets: [
      {
        label: "Глюкоза (ммоль/л)",
        data: filtered.map((h) => Number(h.glucose)),
        tension: 0.4,
        fill: false,
        borderWidth: 3,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        display: true,
      },
      tooltip: {
        mode: "index",
        intersect: false,
      },
    },
    scales: {
      y: {
        beginAtZero: false,
      },
    },
  };

  return (
    <div className="container">
      <h1>История</h1>

      <select
        value={period}
        onChange={(e) => setPeriod(Number(e.target.value))}
      >
        <option value={7}>Последние 7 дней</option>
        <option value={30}>Последние 30 дней</option>
        <option value={90}>Последние 3 месяца</option>
      </select>

      {filtered.length > 0 ? (
        <div className="card">
          <Line data={chartData} options={chartOptions} />
        </div>
      ) : (
        <p className="muted">
          Нет данных за выбранный период
        </p>
      )}

      {/* список измерений (очень полезно врачу) */}
      {filtered.map((h) => (
        <div className="card" key={h.id}>
          <strong>
            {new Date(h.date).toLocaleString("ru-RU")}
          </strong>
          <p>Глюкоза: {h.glucose}</p>
          <p>ХЕ: {h.breadUnits}</p>
          <p>Инсулин: {h.insulin}</p>
        </div>
      ))}
    </div>
  );
}
