import { useEffect, useState } from "react";
import { getProfile, updateProfile } from "../services/api";

export default function Profile({ token }) {
  const [profile, setProfile] = useState(null);
  const [edit, setEdit] = useState(false);
  const [loading, setLoading] = useState(true);

  // –ø–æ–ª—è —Ñ–æ—Ä–º—ã
  const [height, setHeight] = useState("");
  const [weight, setWeight] = useState("");
  const [diseases, setDiseases] = useState("");
  const [photo, setPhoto] = useState("");

  // –∑–∞–≥—Ä—É–∑–∫–∞ –ø—Ä–æ—Ñ–∏–ª—è
  useEffect(() => {
    loadProfile();
    // eslint-disable-next-line
  }, []);

  const loadProfile = async () => {
    const data = await getProfile(token);
    if (data) {
      setProfile(data);
      setHeight(data.height || "");
      setWeight(data.weight || "");
      setDiseases(data.diseases || "");
      setPhoto(data.photo || "");
    }
    setLoading(false);
  };

  const uploadPhoto = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = () => setPhoto(reader.result);
    reader.readAsDataURL(file);
  };

  const save = async () => {
    await updateProfile({ height, weight, diseases, photo }, token);

    // üî• –ö–õ–Æ–ß–ï–í–û–ï –ú–ï–°–¢–û
    const updated = {
      height,
      weight,
      diseases,
      photo,
    };

    setProfile(updated);   // –æ–±–Ω–æ–≤–ª—è–µ–º –∫–∞—Ä—Ç–æ—á–∫—É
    setEdit(false);        // –≤—ã—Ö–æ–¥–∏–º –∏–∑ —Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏—è
  };

  if (loading) {
    return (
      <div className="container">
        <p>–ó–∞–≥—Ä—É–∑–∫–∞ –ø—Ä–æ—Ñ–∏–ª—è...</p>
      </div>
    );
  }

  return (
    <div className="container">
      <h1>–ü—Ä–æ—Ñ–∏–ª—å</h1>

      {/* ===== –ö–ê–†–¢–û–ß–ö–ê –ü–†–û–§–ò–õ–Ø ===== */}
      {!edit && (
        <div className="card">
          {profile?.photo && (
            <img
              src={profile.photo}
              alt="avatar"
              style={{
                width: 120,
                height: 120,
                borderRadius: "50%",
                objectFit: "cover",
                marginBottom: 12,
              }}
            />
          )}

          <p><b>–†–æ—Å—Ç:</b> {profile?.height || "‚Äî"} —Å–º</p>
          <p><b>–í–µ—Å:</b> {profile?.weight || "‚Äî"} –∫–≥</p>

          <p><b>–•—Ä–æ–Ω–∏—á–µ—Å–∫–∏–µ –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏—è:</b></p>
          <p className="muted">
            {profile?.diseases || "–ù–µ —É–∫–∞–∑–∞–Ω—ã"}
          </p>

          <button onClick={() => setEdit(true)}>
            –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å
          </button>
        </div>
      )}

      {/* ===== –§–û–†–ú–ê –†–ï–î–ê–ö–¢–ò–†–û–í–ê–ù–ò–Ø ===== */}
      {edit && (
        <div className="card">
          {photo && (
            <img
              src={photo}
              alt="avatar"
              style={{
                width: 120,
                height: 120,
                borderRadius: "50%",
                objectFit: "cover",
                marginBottom: 12,
              }}
            />
          )}

          <input
            type="file"
            accept="image/*"
            onChange={uploadPhoto}
          />

          <input
            placeholder="–†–æ—Å—Ç (—Å–º)"
            value={height}
            onChange={(e) => setHeight(e.target.value)}
          />

          <input
            placeholder="–í–µ—Å (–∫–≥)"
            value={weight}
            onChange={(e) => setWeight(e.target.value)}
          />

          <textarea
            placeholder="–•—Ä–æ–Ω–∏—á–µ—Å–∫–∏–µ –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏—è"
            value={diseases}
            onChange={(e) => setDiseases(e.target.value)}
          />

          <button onClick={save}>–°–æ—Ö—Ä–∞–Ω–∏—Ç—å</button>
        </div>
      )}
    </div>
  );
}
