import { useEffect, useState } from "react";
import { getPatients, getPatientHistory } from "../services/api";

export default function Patients({ token }) {
  const [patients, setPatients] = useState([]);
  const [history, setHistory] = useState([]);

  useEffect(() => {
    getPatients(token).then(setPatients);
  }, [token]);

  const loadHistory = (id) => {
    getPatientHistory(id, token).then(setHistory);
  };

  return (
    <div className="container">
      <h1>Пациенты</h1>

      {patients.map((p) => (
        <button key={p.id} onClick={() => loadHistory(p.id)}>
          {p.email}
        </button>
      ))}

      {history.map((h) => (
        <div className="result" key={h.id}>
          <p>{h.created_at}</p>
          <strong>{h.dose} ЕД</strong>
          <p>{h.interpretation}</p>
        </div>
      ))}
    </div>
  );
}
