const API = "http://localhost:5000";

// AUTH
export const login = (data) =>
  fetch(`${API}/auth/login`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(data),
  }).then((r) => r.json());

export const register = (data) =>
  fetch(`${API}/auth/register`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(data),
  });

// PROFILE
export const getProfile = (token) =>
  fetch(`${API}/profile`, {
    headers: { Authorization: token },
  }).then((r) => r.json());

export const updateProfile = (data, token) =>
  fetch(`${API}/profile`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Authorization: token,
    },
    body: JSON.stringify(data),
  }).then((r) => r.json());

export const getPatientProfile = (id, token) =>
  fetch(`${API}/profile/patient/${id}`, {
    headers: { Authorization: token },
  }).then((r) => r.json());

export const approvePatient = (id, token) =>
  fetch(`${API}/profile/approve/${id}`, {
    method: "POST",
    headers: { Authorization: token },
  }).then((r) => r.json());

// HISTORY
export const getMyHistory = (token) =>
  fetch(`${API}/calc/my`, {
    headers: { Authorization: token },
  }).then((r) => r.json());

export const getPatientHistory = (id, token) =>
  fetch(`${API}/calc/patients/${id}`, {
    headers: { Authorization: token },
  }).then((r) => r.json());
