import React, { useEffect, useState } from "react";
import { getMyHistory } from "../services/api";
import { Line } from "react-chartjs-2";

export default function History({ token }) {
  const [history, setHistory] = useState([]);

  useEffect(() => {
    if (!token) return;
    getMyHistory(token).then((data) =>
      setHistory(data || [])
    );
  }, [token]);

  const chartData = {
    labels: history.map((h) =>
      new Date(h.created_at).toLocaleDateString()
    ),
    datasets: [
      {
        label: "–ì–ª—é–∫–æ–∑–∞",
        data: history.map((h) => Number(h.glucose)),
        tension: 0.4,
      },
      {
        label: "–ò–Ω—Å—É–ª–∏–Ω",
        data: history.map((h) =>
          h.dose !== null ? Number(h.dose) : null
        ),
        tension: 0.4,
      },
    ],
  };

  return (
    <div className="container">
      <h2>–ò—Å—Ç–æ—Ä–∏—è –∏–∑–º–µ—Ä–µ–Ω–∏–π</h2>

      {history.length > 0 && (
        <div className="card">
          <Line data={chartData} />
        </div>
      )}

      <div className="card">
        {history.map((h) => (
          <div key={h.id} className="history-item">
            <strong>
              {new Date(h.created_at).toLocaleString()}
            </strong>
            <br />
            –°–∞—Ö–∞—Ä: {h.glucose}
            <br />
            –ò–Ω—Å—É–ª–∏–Ω: {h.dose ?? "‚Äî"} –µ–¥.
            <br />

            {h.dose_approved ? (
              <span style={{ color: "green" }}>
                ‚úÖ –ü–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–æ –≤—Ä–∞—á–æ–º
              </span>
            ) : (
              <span style={{ color: "orange" }}>
                ‚è≥ –û–∂–∏–¥–∞–µ—Ç –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏—è
              </span>
            )}

            {h.doctor_comment && (
              <div className="comment">
                üí¨ –ö–æ–º–º–µ–Ω—Ç–∞—Ä–∏–π –≤—Ä–∞—á–∞:{" "}
                {h.doctor_comment}
              </div>
            )}

            <hr />
          </div>
        ))}
      </div>
    </div>
  );
}
